mapper.select:
SELECT
mda.*, ca.CANAME AS CANAME
FROM
t_merch_deta_apply AS mda
LEFT JOIN
t_coop_agency AS ca ON mda.CACODE = ca.CACODE
AND ca.`STATUS` = '00'

	
		AND mda.MEMBER_ID = #{merchDetaApply.memberId}
	
	
		AND mda.MEMBER_NAME = #{merchDetaApply.memberName}
	
	
		AND mda.CACODE = #{merchDetaApply.cacode}
	
	
		And
		
			#{status}
		
	

order by SELF_ID DESC

	limit #{beginRow},#{pageSize}

SELECT COUNT(*) FROM t_merch_deta_apply AS mda

	
		AND mda.MEMBER_ID = #{merchDetaApply.memberId}
	
	
		AND mda.MEMBER_NAME = #{merchDetaApply.memberName}
	
	
		AND mda.CACODE = #{merchDetaApply.cacode}
	
	
		And
		
			#{status}
