mapper.select:
select

	distinct


from t_para_dic

	


	order by ${orderByClause}


	limit #{beginRow},#{pageSize}

mapper.insert:
insert into t_para_dic (TID, PARA_CODE, PARENT_ID,
PARA_NAME, PARA_TYPE, HAS_SUB,
STATUS, ADDED, REMARKS
)
values (#{tid,jdbcType=DECIMAL}, #{paraCode,jdbcType=VARCHAR},
#{parentId,jdbcType=DECIMAL},
#{paraName,jdbcType=VARCHAR}, #{paraType,jdbcType=VARCHAR}, #{hasSub,jdbcType=DECIMAL},
#{status,jdbcType=DECIMAL}, #{added,jdbcType=VARCHAR},
#{remarks,jdbcType=VARCHAR}
)

mapper.select:
select count(*) from t_para_dic where 1=1

	and PARENT_ID=#{parentId}


	and PARA_CODE=#{paraCode}


	and PARA_NAME=#{paraName}

select a.*,b.PARA_NAME parentName

	distinct

from t_para_dic a LEFT JOIN t_para_dic b
on a.PARENT_ID=b.tid where 1=1

	and a.PARENT_ID=#{parentId}


	and a.PARA_CODE=#{paraCode}


	and a.PARA_NAME=#{paraName}


	order by ${orderByClause}

limit #{beginRow},#{pageSize}
