select

  distinct


from t_user

  


  order by ${orderByClause}

limit #{beginRow},#{pageSize}

    SELECT
	a.*, b.USER_NAME creatorName
    
      distinct
    
    FROM
	t_user a
	LEFT JOIN t_user b on b.USER_ID = a.CREATOR 
	where 1=1
    
      and a.USER_NAME=#{userName}
    
    
      and a.USER_CODE=#{userCode}
    
    
      and a.USER_ID in
        
        #{userId}  
      
    
    
      order by ${orderByClause}
    
    limit #{beginRow},#{pageSize}
