mapper.select:
select 

from t_order_collect_single
where MERID = #{merid,jdbcType=VARCHAR} and ORDERID = #{orderid,jdbcType=VARCHAR}

    select 
    
    from 
    	t_order_collect_single
    where 1=1
	    
	       AND MERID = #{merid,jdbcType=VARCHAR}
	    
	    
	       AND TN = #{tn,jdbcType=VARCHAR}
	    
	    
	       AND left(TXNTIME,8) = #{txntime,jdbcType=VARCHAR}
	    
	    
	       AND ORDERID = #{orderid,jdbcType=VARCHAR}
